using PX.Data.EP;
using PX.Data.ReferentialIntegrity.Attributes;
using PX.Data;
using PX.Objects.AR;
using PX.Objects.GL;
using PX.Objects;
using System.Collections.Generic;
using System;

namespace Comissions
{
    public class SalesPersonExt : PXCacheExtension<SalesPerson>
    {
        #region     
        [PXDBString(2)]
        [PXUIField(DisplayName = "Type")]
        [PXStringList(new string[] { "SP", "GP" }, new string[] { "Salesperson", "Group" })]
        public virtual string UsrGroupType { get; set; }
        public abstract class usrGroupType : PX.Data.BQL.BqlString.Field<usrGroupType> { }
        #endregion

        #region UsrGroup
        [PXDBInt]
        [PXUIField(DisplayName = "Group")]
        [PXSelector(typeof(Search<SalesPerson.salesPersonID,Where<SalesPerson.isActive,Equal<True>>>),
            typeof(SalesPerson.salesPersonCD),
            typeof(SalesPerson.descr),
            SubstituteKey =typeof(SalesPerson.salesPersonCD))]
        public virtual int? UsrGroup { get; set; }
        public abstract class usrGroup : PX.Data.BQL.BqlInt.Field<usrGroup> { }
        #endregion
    }
}
